import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const SignupContainer = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
  background-color: ${(props) => props.theme.colors.gray[50]};
`;

const SignupCard = styled.div`
  background-color: white;
  padding: 40px;
  border-radius: ${(props) => props.theme.borderRadius.lg};
  box-shadow: ${(props) => props.theme.shadows.md};
  width: 100%;
  max-width: 800px;
`;

const SignupTitle = styled.h1`
  text-align: center;
  margin-bottom: 10px;
  color: ${(props) => props.theme.colors.dark};
  font-size: 2rem;
`;

const SignupSubtitle = styled.p`
  text-align: center;
  margin-bottom: 40px;
  color: ${(props) => props.theme.colors.gray[600]};
  font-size: 1rem;
`;

const SignupForm = styled.form`
  display: flex;
  flex-direction: column;
  gap: 25px;
`;

const SectionTitle = styled.h3`
  color: ${(props) => props.theme.colors.dark};
  font-size: 1.2rem;
  margin-bottom: 15px;
  padding-bottom: 8px;
  border-bottom: 2px solid ${(props) => props.theme.colors.primary};
`;

const FormRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 20px;

  @media (max-width: ${(props) => props.theme.breakpoints.tablet}) {
    grid-template-columns: 1fr;
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;

  label {
    display: block;
    margin: 8px 0px;
    font-weight: 600;
    color: ${(props) => props.theme.colors.dark};
    font-size: 0.9rem;
  }

  input,
  select {
    width: 100%;
    padding: 14px;
    border: 1px solid ${(props) => props.theme.colors.gray[300]};
    border-radius: ${(props) => props.theme.borderRadius.md};
    font-size: 16px;
    transition: border-color 0.2s ease;

    &:focus {
      border-color: ${(props) => props.theme.colors.primary};
      outline: none;
      box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
    }

    &::placeholder {
      color: ${(props) => props.theme.colors.gray[400]};
    }
  }

  select {
    cursor: pointer;
  }
`;

const SelectWrapper = styled.div`
  position: relative;

  select {
    width: 100%;
    padding: 14px;
    padding-right: 45px;
    border: 1px solid ${(props) => props.theme.colors.gray[300]};
    border-radius: ${(props) => props.theme.borderRadius.md};
    font-size: 16px;
    cursor: pointer;
    appearance: none;
    -webkit-appearance: none;
    -moz-appearance: none;
    background-color: white;
    transition: border-color 0.2s ease;

    &:focus {
      border-color: ${(props) => props.theme.colors.primary};
      outline: none;
      box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
    }
  }

  &::after {
    content: "";
    position: absolute;
    right: 15px;
    top: 50%;
    transform: translateY(-50%) rotate(45deg);
    width: 6px;
    height: 6px;
    border-right: 2px solid ${(props) => props.theme.colors.gray[500]};
    border-bottom: 2px solid ${(props) => props.theme.colors.gray[500]};
    pointer-events: none;
    transition: transform 0.2s ease, border-color 0.2s ease;
  }

  select:focus + * {
    border-color: ${(props) => props.theme.colors.primary};
  }

  &:hover::after {
    border-color: ${(props) => props.theme.colors.gray[700]};
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
`;

const VerifyButton = styled.button`
  padding: 14px 20px;
  background-color: ${(props) => props.theme.colors.secondary || "#6B7280"};
  color: white;
  border: none;
  border-radius: ${(props) => props.theme.borderRadius.md};
  cursor: pointer;
  font-weight: 600;
  font-size: 0.9rem;
  white-space: nowrap;
  transition: background-color 0.2s ease;
  position: relative;

  &:hover {
    background-color: ${(props) =>
      props.theme.colors.secondaryDark || "#4B5563"};
  }

  &:disabled {
    background-color: ${(props) => props.theme.colors.gray[300]};
    cursor: not-allowed;
  }
`;

const TooltipContainer = styled.div`
  position: relative;
  display: inline-block;
`;

const Tooltip = styled.div`
  position: absolute;
  bottom: 120%;
  left: 50%;
  transform: translateX(-50%);
  background-color: ${(props) => props.theme.colors.dark || "#1F2937"};
  color: white;
  padding: 16px;
  border-radius: ${(props) => props.theme.borderRadius.md};
  font-size: 0.8rem;
  white-space: nowrap;
  opacity: 0;
  visibility: hidden;
  transition: opacity 0.3s ease, visibility 0.3s ease;
  z-index: 1000;
  box-shadow: ${(props) => props.theme.shadows.lg};
  min-width: 320px;

  &::after {
    content: "";
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: ${(props) => props.theme.colors.dark || "#1F2937"};
  }

  ${TooltipContainer}:hover & {
    opacity: 1;
    visibility: visible;
  }
`;

const TooltipContent = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
  white-space: normal;
`;

const TooltipTitle = styled.div`
  font-weight: 600;
  font-size: 0.85rem;
  margin: 8px 0px;
  color: #fef3c7;
`;

const DocumentComparison = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 16px;
`;

const DocumentItem = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 8px;
  padding: 8px;
  border-radius: ${(props) => props.theme.borderRadius.sm};
  background-color: ${(props) =>
    props.isCorrect ? "rgba(34, 197, 94, 0.2)" : "rgba(239, 68, 68, 0.2)"};
  border: 1px solid ${(props) => (props.isCorrect ? "#22C55E" : "#EF4444")};
`;

const DocumentIcon = styled.div`
  width: 40px;
  height: 50px;
  background-color: ${(props) => (props.isCorrect ? "#22C55E" : "#EF4444")};
  border-radius: ${(props) => props.theme.borderRadius.sm};
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 20px;
  color: white;
`;

const DocumentLabel = styled.div`
  font-size: 0.75rem;
  font-weight: 500;
  text-align: center;
  color: ${(props) => (props.isCorrect ? "#22C55E" : "#EF4444")};
`;

const StatusIcon = styled.div`
  font-size: 16px;
  color: ${(props) => (props.isCorrect ? "#22C55E" : "#EF4444")};
`;

const PhoneVerificationGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const PhoneInputGroup = styled.div`
  display: flex;
  gap: 10px;
  align-items: end;

  input {
    flex: 1;
  }
`;

const VerificationCodeGroup = styled.div`
  display: flex;
  gap: 10px;
  align-items: end;

  input {
    flex: 1;
  }
`;

const StatusMessage = styled.div`
  font-size: 0.8rem;
  margin-top: 5px;
  color: ${(props) =>
    props.isSuccess
      ? props.theme.colors.success || "#10B981"
      : props.theme.colors.danger || "#EF4444"};
`;

const SignupButton = styled.button`
  padding: 16px;
  background-color: ${(props) => props.theme.colors.primary};
  color: white;
  border: none;
  border-radius: ${(props) => props.theme.borderRadius.md};
  cursor: pointer;
  font-weight: bold;
  font-size: 18px;
  margin-top: 20px;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: ${(props) => props.theme.colors.primaryDark};
  }

  &:disabled {
    background-color: ${(props) => props.theme.colors.gray[300]};
    cursor: not-allowed;
  }
`;

const SignupFooter = styled.div`
  text-align: center;
  margin-top: 30px;

  a {
    color: ${(props) => props.theme.colors.primary};
    text-decoration: none;
    font-weight: 600;

    &:hover {
      text-decoration: underline;
    }
  }
`;

const FileUploadGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const FileUploadContainer = styled.div`
  display: flex;
  /* gap: 10px; */
  align-items: end;
`;

const FileInputWrapper = styled.div`
  position: relative;
  flex: 1;
`;

const HiddenFileInput = styled.input`
  position: absolute;
  opacity: 0;
  width: 100%;
  height: 100%;
  cursor: pointer;
`;

const FileInputDisplay = styled.div`
  width: 100%;
  padding: 10px;
  border: 2px dashed
    ${(props) =>
      props.hasFile
        ? props.theme.colors.primary
        : props.theme.colors.gray[300]};
  border-radius: ${(props) => props.theme.borderRadius.md};
  background-color: ${(props) =>
    props.disabled
      ? props.theme.colors.gray[100]
      : props.hasFile
      ? "rgba(59, 130, 246, 0.05)"
      : props.theme.colors.gray[50]};
  cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
  transition: all 0.2s ease;
  text-align: center;
  color: ${(props) =>
    props.disabled
      ? props.theme.colors.gray[400]
      : props.hasFile
      ? props.theme.colors.primary
      : props.theme.colors.gray[500]};
  opacity: ${(props) => (props.disabled ? 0.6 : 1)};

  &:hover {
    border-color: ${(props) =>
      props.disabled
        ? props.theme.colors.gray[300]
        : props.theme.colors.primary};
    background-color: ${(props) =>
      props.disabled
        ? props.theme.colors.gray[100]
        : "rgba(59, 130, 246, 0.05)"};
  }
`;

const FileInfo = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  font-size: 0.9rem;
`;

const FileIcon = styled.div`
  font-size: 1.2rem;
`;

const UploadButton = styled.button`
  padding: 14px 20px;
  background-color: ${(props) => props.theme.colors.primary};
  color: white;
  border: none;
  border-radius: ${(props) => props.theme.borderRadius.md};
  cursor: pointer;
  font-weight: 600;
  font-size: 0.9rem;
  white-space: nowrap;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: ${(props) => props.theme.colors.primaryDark};
  }

  &:disabled {
    background-color: ${(props) => props.theme.colors.gray[300]};
    cursor: not-allowed;
  }
`;

const LabelWithHelp = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin: 8px 0px;
`;

const LabelLeft = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
`;

const LabelRight = styled.div`
  display: flex;
  align-items: center;
  font-size: 0.8rem;
  gap: 8px;
  color: ${(props) => props.theme.colors.gray[600]};
  cursor: pointer;
  input[type="checkbox"] {
    width: 16px;
    height: 16px;
    margin: 0;
    cursor: pointer;
  }
`;

const CheckboxWrapper = styled.label`
  display: flex;
  align-items: center;
  gap: 6px;
  font-size: 0.8rem;
  color: ${(props) => props.theme.colors.gray[600]};
  cursor: pointer;
  /* line-height: 1.5; */
  input[type="checkbox"] {
    width: 16px;
    height: 16px;
    margin: 0;
    cursor: pointer;
  }
`;

const LabelText = styled.label`
  line-height: 1.5;
  font-weight: 600;
  color: ${(props) => props.theme.colors.dark};
  font-size: 0.9rem;
`;

const HelpIcon = styled.div`
  width: 16px;
  height: 16px;
  border-radius: 50%;
  background-color: ${(props) => props.theme.colors.primary};

  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  color: ${(props) => props.theme.colors.white};
  font-size: 0.9rem;
  cursor: pointer;
  position: relative;
  /* transition: background-color 0.2s ease; */

  &:hover {
    background-color: ${(props) => props.theme.colors.primaryDark};
  }
`;

const HelpTooltip = styled.div`
  position: absolute;
  bottom: 120%;
  left: 50%;
  transform: translateX(-50%);
  background-color: ${(props) => props.theme.colors.dark || "#1F2937"};
  color: white;
  padding: 12px 16px;
  border-radius: ${(props) => props.theme.borderRadius.md};
  font-size: 0.8rem;
  white-space: nowrap;
  opacity: 0;
  visibility: hidden;
  transition: opacity 0.3s ease, visibility 0.3s ease;
  z-index: 1001;
  box-shadow: ${(props) => props.theme.shadows.lg};
  min-width: 320px;

  white-space: normal;

  &::after {
    content: "";
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: ${(props) => props.theme.colors.dark || "#1F2937"};
  }

  ${HelpIcon}:hover & {
    opacity: 1;
    visibility: visible;
  }
`;

const SignupPage = () => {
  const [formData, setFormData] = useState({
    // Í∏∞ÏóÖ Ï†ïÎ≥¥
    businessNumber: "",
    companyName: "",
    representative: "",
    companyAddress: "",
    establishmentDate: "",
    businessType: "",
    businessField: "",
    businessCertificate: null, // ÌååÏùº Ï∂îÍ∞Ä

    // Îã¥ÎãπÏûê Ï†ïÎ≥¥
    managerName: "",
    phoneNumber: "",
    verificationCode: "",

    // Í≥ÑÏ†ï Ï†ïÎ≥¥
    userId: "",
    password: "",
    confirmPassword: "",
  });

  const [verificationStatus, setVerificationStatus] = useState({
    businessVerified: false,
    phoneVerified: false,
    codeSent: false,
    fileUploaded: false,
    attachLater: false, // ÎÇòÏ§ëÏóê Ï≤®Î∂ÄÌïòÍ∏∞ ÏÉÅÌÉú Ï∂îÍ∞Ä
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData((prev) => ({
        ...prev,
        businessCertificate: file,
      }));
      setVerificationStatus((prev) => ({ ...prev, fileUploaded: true }));
      console.log("ÌååÏùº ÏóÖÎ°úÎìú:", file.name);
    }
  };

  const handleAttachLaterChange = (e) => {
    const isChecked = e.target.checked;
    setVerificationStatus((prev) => ({
      ...prev,
      attachLater: isChecked,
      fileUploaded: isChecked, // ÎÇòÏ§ëÏóê Ï≤®Î∂ÄÌïòÍ∏∞ Ï≤¥ÌÅ¨ Ïãú ÌååÏùº ÏóÖÎ°úÎìúÎêú Í≤ÉÏúºÎ°ú Ï≤òÎ¶¨
    }));

    if (isChecked) {
      setFormData((prev) => ({
        ...prev,
        businessCertificate: null, // ÌååÏùº Ï¥àÍ∏∞Ìôî
      }));
    }
  };

  const handleBusinessVerification = () => {
    if (!formData.businessCertificate) {
      alert("ÏÇ¨ÏóÖÏûêÎì±Î°ùÏ¶ùÎ™ÖÏõêÏùÑ Î®ºÏ†Ä Ï≤®Î∂ÄÌï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }
    // ÏÇ¨ÏóÖÏûê Îì±Î°ùÎ≤àÌò∏ Î∞è ÌååÏùº Í≤ÄÏ¶ù Î°úÏßÅ
    console.log("ÏÇ¨ÏóÖÏûê Ïù∏Ï¶ù:", {
      businessNumber: formData.businessNumber,
      file: formData.businessCertificate,
    });
    setVerificationStatus((prev) => ({ ...prev, businessVerified: true }));
  };

  const handlePhoneVerification = () => {
    // Ìú¥ÎåÄÌè∞ Ïù∏Ï¶ùÎ≤àÌò∏ Î∞úÏÜ° Î°úÏßÅ
    console.log("Ïù∏Ï¶ùÎ≤àÌò∏ Î∞úÏÜ°:", formData.phoneNumber);
    setVerificationStatus((prev) => ({ ...prev, codeSent: true }));
  };

  const handleCodeVerification = () => {
    // Ïù∏Ï¶ùÎ≤àÌò∏ ÌôïÏù∏ Î°úÏßÅ
    console.log("Ïù∏Ï¶ùÎ≤àÌò∏ ÌôïÏù∏:", formData.verificationCode);
    setVerificationStatus((prev) => ({ ...prev, phoneVerified: true }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("ÏÇ¨ÏóÖÏûê ÌöåÏõêÍ∞ÄÏûÖ ÏãúÎèÑ:", formData);
  };

  return (
    <SignupContainer>
      <SignupCard>
        <SignupTitle>ÏÇ¨ÏóÖÏûê ÌöåÏõêÍ∞ÄÏûÖ</SignupTitle>
        <SignupSubtitle>
