import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import styled from "styled-components";

const DetailContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: ${(props) => props.theme.spacing.xxl};
  min-height: 100vh;
`;

const BackButton = styled.button`
  background: ${(props) => props.theme.colors.primary};
  color: white;
  border: none;
  padding: ${(props) => props.theme.spacing.sm};
  border-radius: ${(props) => props.theme.borderRadius.md};
  cursor: pointer;
  font-size: 0.9rem;
  margin-bottom: ${(props) => props.theme.spacing.xl};
  transition: background-color 0.3s ease;

  &:hover {
    background: ${(props) => props.theme.colors.primaryDark};
  }
`;

const CategoryHeader = styled.div`
  text-align: center;
  margin-bottom: ${(props) => props.theme.spacing.xxl};
`;

const CategoryIconLarge = styled.div`
  width: 200px;
  height: 200px;
  background: linear-gradient(
    135deg,
    ${(props) => props.color1} 0%,
    ${(props) => props.color2} 100%
  );
  border-radius: ${(props) => props.theme.borderRadius.lg};
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 6rem;
  margin: 0 auto ${(props) => props.theme.spacing.xl};
  box-shadow: ${(props) => props.theme.shadows.lg};

  @media (max-width: ${(props) => props.theme.breakpoints.mobile}) {
    width: 150px;
    height: 150px;
    font-size: 4rem;
  }
`;

const CategoryTitle = styled.h1`
  font-size: 3rem;
  color: ${(props) => props.theme.colors.dark};
  margin-bottom: ${(props) => props.theme.spacing.md};
  font-weight: bold;

  @media (max-width: ${(props) => props.theme.breakpoints.mobile}) {
    font-size: 2rem;
  }
`;

const CategoryDescription = styled.p`
  font-size: 1.2rem;
  color: ${(props) => props.theme.colors.gray[600]};
  line-height: 1.6;
  max-width: 600px;
  margin: 0 auto ${(props) => props.theme.spacing.xxl};
`;

const ContentSection = styled.div`
  background: white;
  padding: ${(props) => props.theme.spacing.xxl};
  border-radius: ${(props) => props.theme.borderRadius.lg};
  box-shadow: ${(props) => props.theme.shadows.sm};
  margin-bottom: ${(props) => props.theme.spacing.xl};
`;

const SectionTitle = styled.h2`
  font-size: 2rem;
  color: ${(props) => props.theme.colors.dark};
  margin-bottom: ${(props) => props.theme.spacing.lg};
  text-align: center;
`;

const PlaceholderContent = styled.div`
  text-align: center;
  color: ${(props) => props.theme.colors.gray[500]};
  font-size: 1.1rem;
  padding: ${(props) => props.theme.spacing.xxl};
  background: ${(props) => props.theme.colors.gray[50]};
  border-radius: ${(props) => props.theme.borderRadius.md};
`;

const CategoryDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  // Ïπ¥ÌÖåÍ≥†Î¶¨ Îç∞Ïù¥ÌÑ∞ (Ïã§Ï†úÎ°úÎäî APIÏóêÏÑú Í∞ÄÏ†∏Ïò§Í±∞ÎÇò Context/ReduxÏóêÏÑú Í¥ÄÎ¶¨)
  const categories = [
    {
      id: 1,
      name: "Ìò∏ÌÖî",
      description: "Îü≠ÏÖîÎ¶¨ÌïòÍ≥† Ìé∏ÏïàÌïú Ìò∏ÌÖîÏóêÏÑúÏùò ÌäπÎ≥ÑÌïú Í≤ΩÌóòÏùÑ Ï†úÍ≥µÌï©ÎãàÎã§.",
      icon: "üè®",
      color1: "#FF6B6B",
      color2: "#FF8E8E",
    },
    {
      id: 2,
      name: "ÌéúÏÖò",
      description: "ÏûêÏó∞ ÏÜçÏóêÏÑú ÌûêÎßÅÌï† Ïàò ÏûàÎäî ÏïÑÎäëÌïú ÌéúÏÖòÏùÑ Ï∂îÏ≤úÌï©ÎãàÎã§.",
      icon: "üè°",
      color1: "#4ECDC4",
      color2: "#6ED3D0",
    },
    {
      id: 3,
      name: "Î¶¨Ï°∞Ìä∏",
      description: "Ìú¥ÏñëÍ≥º Î†àÏ†ÄÎ•º ÎèôÏãúÏóê Ï¶êÍ∏∏ Ïàò ÏûàÎäî ÌîÑÎ¶¨ÎØ∏ÏóÑ Î¶¨Ï°∞Ìä∏ÏûÖÎãàÎã§.",
      icon: "üèñÔ∏è",
      color1: "#45B7D1",
      color2: "#6BC5D8",
    },
    {
      id: 4,
      name: "ÎØºÎ∞ï",
      description: "ÌòÑÏßÄÏùò Ï†ïÏ∑®Î•º ÎäêÎÇÑ Ïàò ÏûàÎäî Îî∞ÎúªÌïú ÎØºÎ∞ïÏùÑ ÏÜåÍ∞úÌï©ÎãàÎã§.",
      icon: "üè†",
      color1: "#96CEB4",
      color2: "#A8D4C2",
    },
    {
      id: 5,
      name: "Ï∫†Ìïë",
      description: "ÏûêÏó∞Í≥º ÌïòÎÇòÎêòÎäî Ï∫†ÌïëÏùò Ï¶êÍ±∞ÏõÄÏùÑ ÎßåÎÅΩÌïòÏÑ∏Ïöî.",
      icon: "‚õ∫",
      color1: "#FECA57",
      color2: "#FED766",
    },
    {
      id: 6,
      name: "Í≤åÏä§Ìä∏ÌïòÏö∞Ïä§",
      description: "ÏÉàÎ°úÏö¥ ÏÇ¨ÎûåÎì§Í≥ºÏùò ÎßåÎÇ®Ïù¥ ÏûàÎäî Í≤åÏä§Ìä∏ÌïòÏö∞Ïä§ÏûÖÎãàÎã§.",
      icon: "üèØ",
      color1: "#FF9FF3",
      color2: "#FFB3F7",
    },
    {
      id: 7,
      name: "Í∏ÄÎû®Ìïë",
      description: "Ìé∏ÏïàÌï®Í≥º ÏûêÏó∞ÏùÑ ÎèôÏãúÏóê Ï¶êÍ∏∞Îäî Í∏ÄÎû®Ìïë Ï≤¥ÌóòÏûÖÎãàÎã§.",
      icon: "üèØ",
      color1: "#A8E6CF",
      color2: "#B8F0D6",
    },
    {
      id: 8,
      name: "Î™®ÌÖî",
      description: "Ìé∏Î¶¨Ìïú Ï†ëÍ∑ºÏÑ±Í≥º Ìï©Î¶¨Ï†ÅÏù∏ Í∞ÄÍ≤©Ïùò Î™®ÌÖîÏûÖÎãàÎã§.",
      icon: "üèØ",
      color1: "#FFD93D",
      color2: "#FFE066",
    },
  ];

  const category = categories.find((cat) => cat.id === parseInt(id));

  if (!category) {
    return (
      <DetailContainer>
        <BackButton onClick={() => navigate("/")}>
          ‚Üê Î©îÏù∏ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
        </BackButton>
        <CategoryHeader>
          <CategoryTitle>Ïπ¥ÌÖåÍ≥†Î¶¨Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§</CategoryTitle>
          <CategoryDescription>
            Ï°¥Ïû¨ÌïòÏßÄ ÏïäÎäî Ïπ¥ÌÖåÍ≥†Î¶¨ÏûÖÎãàÎã§.
          </CategoryDescription>
        </CategoryHeader>
      </DetailContainer>
