  }

  return (
    <DetailContainer>
      <BackButton onClick={() => navigate("/")}>‚Üê Î©îÏù∏ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</BackButton>

      <CategoryHeader>
        <CategoryIconLarge color1={category.color1} color2={category.color2}>
          {category.icon}
        </CategoryIconLarge>
        <CategoryTitle>{category.name}</CategoryTitle>
        <CategoryDescription>{category.description}</CategoryDescription>
      </CategoryHeader>

      <ContentSection>
        <SectionTitle>Ï∂îÏ≤ú ÏàôÏÜå</SectionTitle>
        <PlaceholderContent>
          {category.name} Ïπ¥ÌÖåÍ≥†Î¶¨Ïùò Ï∂îÏ≤ú ÏàôÏÜåÎì§Ïù¥ Ïó¨Í∏∞Ïóê ÌëúÏãúÎê©ÎãàÎã§.
          <br />
          Ìñ•ÌõÑ ÏàôÏÜå Î™©Î°ù, ÌïÑÌÑ∞ÎßÅ, ÏòàÏïΩ Í∏∞Îä• Îì±ÏùÑ Ï∂îÍ∞ÄÌï† Ïàò ÏûàÏäµÎãàÎã§.
        </PlaceholderContent>
      </ContentSection>

      <ContentSection>
        <SectionTitle>Ïù∏Í∏∞ ÏßÄÏó≠</SectionTitle>
        <PlaceholderContent>
          {category.name} Í¥ÄÎ†® Ïù∏Í∏∞ Ïó¨ÌñâÏßÄÏôÄ ÏßÄÏó≠ Ï†ïÎ≥¥Í∞Ä ÌëúÏãúÎê©ÎãàÎã§.
        </PlaceholderContent>
      </ContentSection>

