const MyPage = () => {
  const [activeTab, setActiveTab] = useState("posts");
  const [userInfo, setUserInfo] = useState({
    name: "",
    email: "",
    phone: "",
    companyName: "",
    businessNumber: "",
    representative: "",
    companyAddress: "",
    businessType: "",
    businessField: "",
    managerName: "",
  });
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);

  const [selectedPost, setSelectedPost] = useState(null);
  const [modalType, setModalType] = useState(null); // 'preview', 'delete'

  // Firebase에서 사용자 정보 로드
  useEffect(() => {
    const loadUserInfo = async () => {
      try {
        if (!isUserLoggedIn()) {
          console.log("로그인되지 않은 사용자");
          setIsLoading(false);
          return;
        }

        const currentUser = getCurrentUser();
        const currentUserInfo = getCurrentUserInfo();

        if (currentUser && currentUserInfo) {
          console.log("로컬 스토리지에서 사용자 정보 로드:", currentUserInfo);
          setUserInfo({
            name: currentUserInfo.name || "",
            email: currentUser.email || "",
            phone: currentUserInfo.phone || "",
            companyName: currentUserInfo.companyName || "",
            businessNumber: currentUserInfo.businessNumber || "",
            representative: currentUserInfo.representative || "",
            companyAddress: currentUserInfo.companyAddress || "",
            businessType: currentUserInfo.businessType || "",
            businessField: currentUserInfo.businessField || "",
            managerName: currentUserInfo.managerName || "",
          });
        } else {
          console.log("로컬 스토리지에 정보가 없어서 Firestore에서 다시 로드");
          // 로컬 스토리지에 정보가 없으면 Firestore에서 직접 가져오기
          const freshUserInfo = await getUserInfo(currentUser?.uid);
          if (freshUserInfo) {
            setUserInfo({
              name: freshUserInfo.name || "",
              email: currentUser?.email || "",
              phone: freshUserInfo.phone || "",
              companyName: freshUserInfo.companyName || "",
              businessNumber: freshUserInfo.businessNumber || "",
              representative: freshUserInfo.representative || "",
              companyAddress: freshUserInfo.companyAddress || "",
              businessType: freshUserInfo.businessType || "",
              businessField: freshUserInfo.businessField || "",
              managerName: freshUserInfo.managerName || "",
            });
          }
        }
      } catch (error) {
        console.error("사용자 정보 로드 실패:", error);
      } finally {
        setIsLoading(false);
      }
    };

    loadUserInfo();
  }, []);

  const menuItems = [
    { id: "posts", label: "게시물 관리" },
    { id: "settings", label: "계정 설정" },
  ];

  // 목업 게시물 데이터 - 서비스 등록 페이지와 동일한 구조
  const [posts, setPosts] = useState([
    {
      id: 1,
      serviceName: "서비스명",
      companyWebsite: "https://example.com",
      price: "120,000원",
      isPricingOptional: false,
      serviceRegion: "서울특별시 강남구",
      serviceDescription: "설명",
      categories: ["카테고리1", "카테고리2"],
      tags: ["태그1", "태그2", "태그3"],
      files: [
        { name: "파일1.jpg" },
        { name: "파일2.jpg" },
        { name: "파일3.pdf" },
      ],
      freePostContent: "서비스 기본 정보 - 문의: 064-123-4567",
      status: "active",
      createdAt: "2024-01-15",
      views: 1234,
    },
    {
      id: 2,
      serviceName: "서비스명",
      companyWebsite: "https://example.com",
      price: "120,000원",
      isPricingOptional: false,
      serviceRegion: "서울특별시 강남구",
      serviceDescription: "설명",
      categories: ["카테고리1", "카테고리2"],
      tags: ["태그1", "태그2", "태그3"],
      files: [
        { name: "파일1.jpg" },
        { name: "파일2.jpg" },
        { name: "파일3.pdf" },
        { name: "파일4.pdf" },
        { name: "파일5.pdf" },
        { name: "파일6.pdf" },
        { name: "파일7.pdf" },
        { name: "파일8.pdf" },
      ],
      freePostContent: "서비스 기본 정보 - 문의: 064-123-4567",
      status: "pending",
      createdAt: "2024-01-10",
      views: 856,
    },
    {
      id: 3,
      serviceName: "서비스명",
      companyWebsite: "https://example.com",
      price: "120,000원",
      isPricingOptional: true,
      serviceRegion: "서울특별시 강남구",
      serviceDescription: "설명",
      categories: ["카테고리1", "카테고리2"],
      tags: ["태그1", "태그2", "태그3"],
      files: [{ name: "파일1.jpg" }, { name: "파일2.jpg" }],
      freePostContent: "서비스 기본 정보 - 문의: 064-123-4567",
      status: "active",
      createdAt: "2024-01-08",
      views: 567,
    },
  ]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserInfo((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSave = () => {
    console.log("프로필 저장:", userInfo);
    alert("계정 정보가 저장되었습니다.");
  };

  const openModal = (type, post = null) => {
    setModalType(type);
    setSelectedPost(post);
  };

  const closeModal = () => {
    setModalType(null);
    setSelectedPost(null);
  };

  const handleDeletePost = (postId) => {
    setPosts((prev) => prev.filter((post) => post.id !== postId));
    closeModal();
  };

  const getStatusBadge = (status) => {
    const statusMap = {
      active: { text: "게시중", color: "#10B981" },
      pending: { text: "검토중", color: "#F59E0B" },
      rejected: { text: "반려됨", color: "#EF4444" },
    };
    return statusMap[status] || statusMap.pending;
  };

  const getCategoryNames = (categoryIds) => {
    const categoryMap = {
      hotel: "호텔/리조트",
      pension: "펜션/민박",
      guesthouse: "게스트하우스",
      camping: "캠핑/글램핑",
      motel: "모텔",
      experience: "체험/액티비티",
      food: "맛집/카페",
      transportation: "교통/렌터카",
    };
    return categoryIds.map((id) => categoryMap[id] || id).join(", ");
  };

  const renderModal = () => {
    if (!modalType || !selectedPost) return null;

    switch (modalType) {
      case "preview":
        return (
          <ModalOverlay onClick={closeModal}>
            <ModalContent width="700px" onClick={(e) => e.stopPropagation()}>
              <ModalHeader>
                <h3>게시물 미리보기</h3>
                <CloseButton onClick={closeModal}>×</CloseButton>
              </ModalHeader>

              <PreviewSection>
                <h4>서비스명</h4>
                <p>{selectedPost.serviceName}</p>
              </PreviewSection>

              {selectedPost.companyWebsite && (
                <PreviewSection>
                  <h4>홈페이지</h4>
                  <p>{selectedPost.companyWebsite}</p>
                </PreviewSection>
              )}

              {!selectedPost.isPricingOptional && selectedPost.price && (
                <PreviewSection>
                  <h4>가격</h4>
                  <p>{selectedPost.price}</p>
                </PreviewSection>
              )}

              <PreviewSection>
                <h4>서비스 지역</h4>
                <p>{selectedPost.serviceRegion}</p>
              </PreviewSection>

              <PreviewSection>
                <h4>설명</h4>
                <p>{selectedPost.serviceDescription}</p>
              </PreviewSection>

              <PreviewSection>
                <h4>카테고리</h4>
                <p>{getCategoryNames(selectedPost.categories)}</p>
              </PreviewSection>

              <PreviewSection>
                <h4>태그</h4>
                <TagContainer>
                  {selectedPost.tags.map((tag, index) => (
                    <Tag key={index}>#{tag}</Tag>
                  ))}
                </TagContainer>
              </PreviewSection>

              <PreviewSection>
                <h4>첨부 파일</h4>
                <div>
                  {selectedPost.files.map((file, index) => (
                    <p
                      key={index}
                      style={{ margin: "4px 0", fontSize: "0.85rem" }}
                    >
                      📎 {file.name}
                    </p>
                  ))}
                </div>
              </PreviewSection>

              {selectedPost.freePostContent && (
                <PreviewSection>
                  <h4>무료 서비스 종료 시 표시 내용</h4>
                  <p>{selectedPost.freePostContent}</p>
                </PreviewSection>
              )}
            </ModalContent>
          </ModalOverlay>
        );

      case "delete":
        return (
          <ModalOverlay onClick={closeModal}>
            <ModalContent width="400px" onClick={(e) => e.stopPropagation()}>
              <ModalHeader>
                <h3>게시물 삭제</h3>
                <CloseButton onClick={closeModal}>×</CloseButton>
              </ModalHeader>

              <div style={{ textAlign: "center", padding: "20px 0" }}>
                <p style={{ marginBottom: "20px", fontSize: "1.1rem" }}>
                  정말로 "{selectedPost.serviceName}" 게시물을 삭제하시겠습니까?
                </p>
                <p
                  style={{
                    color: "#EF4444",
                    fontSize: "0.9rem",
                    marginBottom: "30px",
                  }}
                >
                  이 작업은 되돌릴 수 없습니다.
                </p>
                <div
                  style={{
                    display: "flex",
                    gap: "10px",
                    justifyContent: "center",
                  }}
                >
                  <ActionButton onClick={closeModal}>취소</ActionButton>
                  <ActionButton
                    variant="danger"
                    onClick={() => handleDeletePost(selectedPost.id)}
                  >
                    삭제
                  </ActionButton>
                </div>
              </div>
            </ModalContent>
          </ModalOverlay>
        );

      default:
        return null;
    }
  };

  const renderContent = () => {
    switch (activeTab) {
      case "posts":
        return (
          <div>
            <PostsHeader>
              <SectionTitle>게시물 관리</SectionTitle>
              <AddPostButton
                onClick={() => window.open("/service-register", "_blank")}
              >
                + 새 게시물 작성
              </AddPostButton>
            </PostsHeader>

            {posts.length === 0 ? (
              <EmptyState>
                <h3>등록된 게시물이 없습니다</h3>
                <p>새로운 서비스를 등록하여 고객들에게 소개해보세요!</p>
                <AddPostButton
                  onClick={() => window.open("/service-register", "_blank")}
                >
                  첫 게시물 작성하기
                </AddPostButton>
              </EmptyState>
            ) : (
              posts.map((post) => {
                const status = getStatusBadge(post.status);
                return (
                  <PostCard key={post.id}>
                    <PostHeader>
                      <h4>{post.serviceName}</h4>
                      <PostActions>
                        <ActionButton
                          onClick={() => openModal("preview", post)}
                        >
                          미리보기
                        </ActionButton>
                        <ActionButton
                          variant="warning"
                          onClick={() =>
                            window.open("/service-register", "_blank")
                          }
                        >
                          수정
                        </ActionButton>
                        <ActionButton
                          variant="danger"
                          onClick={() => openModal("delete", post)}
                        >
                          삭제
                        </ActionButton>
                      </PostActions>
                    </PostHeader>

                    <PostMeta>
                      <span>📅 {post.createdAt}</span>
                      <span>👁️ {post.views.toLocaleString()}회</span>
                      {!post.isPricingOptional && post.price && (
                        <span>💰 {post.price}</span>
                      )}
                      <span>📍 {post.serviceRegion}</span>
                      <span>📁 {post.files.length}개 파일</span>
                      <StatusBadge
                        style={{
                          backgroundColor: status.color + "20",
                          color: status.color,
                        }}
                      >
                        {status.text}
                      </StatusBadge>
                    </PostMeta>

                    <PostDescription>{post.serviceDescription}</PostDescription>

                    <TagContainer>
                      {post.tags.slice(0, 3).map((tag, index) => (
                        <Tag key={index}>#{tag}</Tag>
                      ))}
                      {post.tags.length > 3 && (
                        <Tag>+{post.tags.length - 3}개 더</Tag>
                      )}
                    </TagContainer>
                  </PostCard>
                );
              })
            )}
          </div>
        );

      case "settings":
        return (
          <div>
            <SectionTitle>계정 설정</SectionTitle>
            <FormGrid>
              <FormGroup>
                <label>이름 *</label>
                <input
                  type="text"
                  name="name"
                  value={userInfo.name}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>
              <FormGroup>
                <label>이메일 *</label>
                <input
                  type="email"
                  name="email"
                  value={userInfo.email}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>
              <FormGroup>
                <label>전화번호 *</label>
                <input
                  type="tel"
                  name="phone"
                  value={userInfo.phone}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>
              <FormGroup>
                <label>기업명</label>
                <input
                  type="text"
                  name="companyName"
                  value={userInfo.companyName}
                  onChange={handleInputChange}
                />
              </FormGroup>
            </FormGrid>
            <FormGroup style={{ marginBottom: "20px" }}>
              <label>사업자 등록번호</label>
              <input
                type="text"
                name="businessNumber"
                value={userInfo.businessNumber}
                onChange={handleInputChange}
                placeholder="123-45-67890"
              />
            </FormGroup>
            <SaveButton onClick={handleSave}>변경사항 저장</SaveButton>
          </div>
        );

      default:
        return (
          <div>
            <SectionTitle>준비 중</SectionTitle>
            <p>해당 기능은 준비 중입니다.</p>
          </div>
        );
    }
  };

  return (
    <MyPageContainer>
      <PageHeader>
        <h1>마이페이지</h1>
        <p>게시물과 계정 정보를 관리할 수 있습니다.</p>
      </PageHeader>

      <ContentGrid>
        <Sidebar>
          <SidebarMenu>
            {menuItems.map((item) => (
              <MenuItem key={item.id}>
                <MenuButton
                  active={activeTab === item.id}
                  onClick={() => setActiveTab(item.id)}
                >
                  {item.label}
                </MenuButton>
              </MenuItem>
            ))}
          </SidebarMenu>
        </Sidebar>

        <MainContent>{renderContent()}</MainContent>
      </ContentGrid>

      {renderModal()}
    </MyPageContainer>
  );
};

export default MyPage;
