import React, { useState, useEffect } from "react";
import styled from "styled-components";
import ImageSlide from "../components/main/ImageSlide";
import CategoryList from "../components/main/CategoryList";
import SearchBar from "../components/main/SearchBar";
import {
  isUserLoggedIn,
  getCurrentUserToken,
  getCurrentUser,
  getCurrentUserInfo,
  getLoginTime,
} from "../firebase/auth";

const MainContainer = styled.div`
  min-height: 80vh;
`;

const SlideSection = styled.div`
  width: 100%;
  overflow: hidden;
  margin: 0 auto;
`;

const SearchSection = styled.div`
  text-align: center;
  padding: 20px 0;
  background-color: #f0f2f5;
`;

const SearchTitle = styled.h2`
  margin-bottom: 20px;
  color: #333;
`;

const CategorySection = styled.div`
  padding: 20px;
  background-color: #f8f9fa;
`;

const CategoryTitle = styled.h2`
  margin-bottom: 20px;
  color: #333;
`;

const DebugInfo = styled.div`
  background-color: #f8f9fa;
  border: 1px solid #dee2e6;
  border-radius: 8px;
  padding: 16px;
  margin: 20px 0;
  font-family: monospace;
  font-size: 12px;

  h4 {
    margin: 0 0 10px 0;
    color: #333;
  }

  pre {
    margin: 0;
    white-space: pre-wrap;
    word-break: break-all;
  }
`;

const MainPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [authDebugInfo, setAuthDebugInfo] = useState(null);

  useEffect(() => {
    // ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏
    const updateDebugInfo = () => {
      const debugInfo = {
        isLoggedIn: isUserLoggedIn(),
        token: getCurrentUserToken(),
        user: getCurrentUser(),
        userInfo: getCurrentUserInfo(),
        loginTime: getLoginTime(),
      };
      setAuthDebugInfo(debugInfo);
    };

    updateDebugInfo();

    // 5Ï¥àÎßàÎã§ ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏ (ÌÜ†ÌÅ∞ ÏÉÅÌÉú ÌôïÏù∏Ïö©)
    const interval = setInterval(updateDebugInfo, 5000);

    return () => clearInterval(interval);
  }, []);

  const handleSearch = (term) => {
    setSearchTerm(term);
    console.log("Í≤ÄÏÉâÏñ¥:", term);
  };

  return (
    <MainContainer>
      <SlideSection>
        <ImageSlide />
      </SlideSection>

      <SearchSection>
        <SearchTitle>ÏõêÌïòÎäî ÏÑúÎπÑÏä§Î•º Ï∞æÏïÑÎ≥¥ÏÑ∏Ïöî</SearchTitle>
        <SearchBar onSearch={handleSearch} />
      </SearchSection>

      {/* Í∞úÎ∞úÏö© ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ */}
      {process.env.NODE_ENV === "development" && authDebugInfo && (
        <DebugInfo>
          <h4>üîç Ïù∏Ï¶ù ÏÉÅÌÉú ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥</h4>
          <pre>{JSON.stringify(authDebugInfo, null, 2)}</pre>
        </DebugInfo>
      )}

      <CategorySection>
        <CategoryTitle>Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ ÏÑúÎπÑÏä§</CategoryTitle>
        <CategoryList searchTerm={searchTerm} />
      </CategorySection>
    </MainContainer>
  );
};

export default MainPage;
