import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const SignupContainer = styled.div`
  min-height: 80vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: ${(props) => props.theme.spacing.lg};
  background: ${(props) => props.theme.colors.gray[100]};
`;

const SignupCard = styled.div`
  background: ${(props) => props.theme.colors.white};
  padding: ${(props) => props.theme.spacing.xxl};
  border-radius: ${(props) => props.theme.borderRadius.lg};
  box-shadow: ${(props) => props.theme.shadows.lg};
  width: 100%;
  max-width: 500px;
`;

const SignupTitle = styled.h1`
  text-align: center;
  color: ${(props) => props.theme.colors.dark};
  margin-bottom: ${(props) => props.theme.spacing.xl};
  font-size: 2rem;
`;

const SignupForm = styled.form`
  display: flex;
  flex-direction: column;
  gap: ${(props) => props.theme.spacing.lg};
`;

const FormRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${(props) => props.theme.spacing.md};

  @media (max-width: ${(props) => props.theme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;

  label {
    font-weight: 600;
    color: ${(props) => props.theme.colors.dark};
    margin-bottom: ${(props) => props.theme.spacing.xs};
  }

  input {
    padding: ${(props) => props.theme.spacing.md};
    border: 2px solid ${(props) => props.theme.colors.gray[300]};
    border-radius: ${(props) => props.theme.borderRadius.md};
    font-size: 1rem;
    transition: border-color 0.2s ease;

    &:focus {
      border-color: ${(props) => props.theme.colors.primary};
    }

    &::placeholder {
      color: ${(props) => props.theme.colors.gray[500]};
    }
  }
`;

const PasswordStrength = styled.div`
  margin-top: ${(props) => props.theme.spacing.xs};
  font-size: 0.8rem;

  span {
    padding: 2px 6px;
    border-radius: 3px;
    margin-right: ${(props) => props.theme.spacing.xs};

    &.weak {
      background: ${(props) => props.theme.colors.error}20;
      color: ${(props) => props.theme.colors.error};
    }

    &.medium {
      background: ${(props) => props.theme.colors.warning}20;
      color: ${(props) => props.theme.colors.warning};
    }

    &.strong {
      background: ${(props) => props.theme.colors.success}20;
      color: ${(props) => props.theme.colors.success};
    }
  }
`;

const CheckboxGroup = styled.div`
  display: flex;
  align-items: flex-start;
  gap: ${(props) => props.theme.spacing.sm};

  input[type="checkbox"] {
    margin-top: 2px;
  }

  label {
    font-size: 0.9rem;
    color: ${(props) => props.theme.colors.gray[700]};
    line-height: 1.4;

    a {
      color: ${(props) => props.theme.colors.primary};
      text-decoration: underline;
    }
  }
`;

const SignupButton = styled.button`
  background: ${(props) => props.theme.colors.primary};
  color: ${(props) => props.theme.colors.white};
  padding: ${(props) => props.theme.spacing.md};
  border-radius: ${(props) => props.theme.borderRadius.md};
  font-size: 1.1rem;
  font-weight: 600;
  transition: background 0.2s ease;

  &:hover:not(:disabled) {
    background: ${(props) => props.theme.colors.primary}dd;
  }

  &:disabled {
    background: ${(props) => props.theme.colors.gray[400]};
    cursor: not-allowed;
  }
`;

const SignupFooter = styled.div`
  text-align: center;
  margin-top: ${(props) => props.theme.spacing.lg};
  color: ${(props) => props.theme.colors.gray[600]};

  a {
    color: ${(props) => props.theme.colors.primary};
    font-weight: 600;

    &:hover {
      text-decoration: underline;
    }
  }
`;

const ErrorMessage = styled.div`
  background: ${(props) => props.theme.colors.error}20;
  color: ${(props) => props.theme.colors.error};
  padding: ${(props) => props.theme.spacing.sm};
  border-radius: ${(props) => props.theme.borderRadius.md};
  font-size: 0.9rem;
  text-align: center;
`;

const SuccessMessage = styled.div`
  background: ${(props) => props.theme.colors.success}20;
  color: ${(props) => props.theme.colors.success};
  padding: ${(props) => props.theme.spacing.sm};
  border-radius: ${(props) => props.theme.borderRadius.md};
  font-size: 0.9rem;
  text-align: center;
`;

const SignupPage = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
    phone: "",
    agreeTerms: false,
    agreeMarketing: false,
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const getPasswordStrength = (password) => {
    if (password.length < 6) return "weak";
    if (
      password.length < 10 ||
      !/(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/.test(password)
    )
      return "medium";
    return "strong";
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
    setError("");
    setSuccess("");
  };

  const validateForm = () => {
    if (!formData.firstName || !formData.lastName) {
      throw new Error("이름을 모두 입력해주세요.");
    }

    if (!formData.email || !formData.email.includes("@")) {
      throw new Error("올바른 이메일을 입력해주세요.");
    }

    if (formData.password.length < 6) {
      throw new Error("비밀번호는 최소 6자 이상이어야 합니다.");
    }

    if (formData.password !== formData.confirmPassword) {
      throw new Error("비밀번호가 일치하지 않습니다.");
    }

    if (!formData.agreeTerms) {
      throw new Error("이용약관에 동의해주세요.");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError("");
    setSuccess("");

    try {
      validateForm();

      // 여기에 회원가입 API 호출
      console.log("회원가입 데이터:", formData);

      // 임시 처리
      setTimeout(() => {
        setIsLoading(false);
        setSuccess("회원가입이 완료되었습니다! 이메일을 확인해주세요.");
      }, 1000);
    } catch (err) {
      setError(err.message);
      setIsLoading(false);
    }
  };

  return (
    <SignupContainer>
      <SignupCard>
        <SignupTitle>회원가입</SignupTitle>

        {error && <ErrorMessage>{error}</ErrorMessage>}
        {success && <SuccessMessage>{success}</SuccessMessage>}

        <SignupForm onSubmit={handleSubmit}>
          <FormRow>
            <FormGroup>
              <label htmlFor="firstName">성</label>
              <input
                type="text"
                id="firstName"
                name="firstName"
                placeholder="성을 입력하세요"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
            </FormGroup>

            <FormGroup>
              <label htmlFor="lastName">이름</label>
              <input
                type="text"
                id="lastName"
                name="lastName"
                placeholder="이름을 입력하세요"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
            </FormGroup>
          </FormRow>

          <FormGroup>
            <label htmlFor="email">이메일</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="이메일을 입력하세요"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="phone">전화번호</label>
            <input
              type="tel"
              id="phone"
              name="phone"
              placeholder="전화번호를 입력하세요"
              value={formData.phone}
              onChange={handleInputChange}
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="password">비밀번호</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="비밀번호를 입력하세요"
              value={formData.password}
              onChange={handleInputChange}
              required
            />
            {formData.password && (
              <PasswordStrength>
                <span className={getPasswordStrength(formData.password)}>
                  {getPasswordStrength(formData.password) === "weak" && "약함"}
                  {getPasswordStrength(formData.password) === "medium" &&
                    "보통"}
                  {getPasswordStrength(formData.password) === "strong" &&
                    "강함"}
                </span>
              </PasswordStrength>
            )}
          </FormGroup>

          <FormGroup>
            <label htmlFor="confirmPassword">비밀번호 확인</label>
            <input
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              placeholder="비밀번호를 다시 입력하세요"
              value={formData.confirmPassword}
              onChange={handleInputChange}
              required
            />
          </FormGroup>

          <CheckboxGroup>
            <input
              type="checkbox"
              id="agreeTerms"
              name="agreeTerms"
              checked={formData.agreeTerms}
              onChange={handleInputChange}
              required
            />
            <label htmlFor="agreeTerms">
              <a href="#terms">이용약관</a> 및{" "}
              <a href="#privacy">개인정보 처리방침</a>에 동의합니다 (필수)
            </label>
          </CheckboxGroup>

          <CheckboxGroup>
            <input
              type="checkbox"
              id="agreeMarketing"
              name="agreeMarketing"
              checked={formData.agreeMarketing}
              onChange={handleInputChange}
            />
            <label htmlFor="agreeMarketing">
              마케팅 정보 수신에 동의합니다 (선택)
            </label>
          </CheckboxGroup>

          <SignupButton
            type="submit"
            disabled={isLoading || !formData.agreeTerms}
          >
            {isLoading ? "가입 중..." : "회원가입"}
          </SignupButton>
        </SignupForm>

        <SignupFooter>
          <p>
            이미 계정이 있으신가요? <Link to="/login">로그인</Link>
          </p>
        </SignupFooter>
      </SignupCard>
    </SignupContainer>
  );
};

export default SignupPage;
