const SignupPage = () => {
  const [formData, setFormData] = useState({
    // 기업 정보
    businessNumber: "",
    companyName: "",
    representative: "",
    companyAddress: "",
    establishmentDate: "",
    businessType: "",
    businessCertificate: null, // 파일 추가

    // 담당자 정보
    managerName: "",
    phoneNumber: "",
    verificationCode: "",

    // 계정 정보
    userId: "",
    password: "",
    confirmPassword: "",
  });

  const [verificationStatus, setVerificationStatus] = useState({
    businessVerified: false,
    phoneVerified: false,
    codeSent: false,
    fileUploaded: false, // 파일 업로드 상태 추가
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData((prev) => ({
        ...prev,
        businessCertificate: file,
      }));
      setVerificationStatus((prev) => ({ ...prev, fileUploaded: true }));
      console.log("파일 업로드:", file.name);
    }
  };

  const handleBusinessVerification = () => {
    if (!formData.businessCertificate) {
      alert("사업자등록증명원을 먼저 첨부해주세요.");
      return;
    }
    // 사업자 등록번호 및 파일 검증 로직
    console.log("사업자 인증:", {
      businessNumber: formData.businessNumber,
      file: formData.businessCertificate,
    });
    setVerificationStatus((prev) => ({ ...prev, businessVerified: true }));
  };

  const handlePhoneVerification = () => {
    // 휴대폰 인증번호 발송 로직
    console.log("인증번호 발송:", formData.phoneNumber);
    setVerificationStatus((prev) => ({ ...prev, codeSent: true }));
  };

  const handleCodeVerification = () => {
    // 인증번호 확인 로직
    console.log("인증번호 확인:", formData.verificationCode);
    setVerificationStatus((prev) => ({ ...prev, phoneVerified: true }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("사업자 회원가입 시도:", formData);
  };

  return (
    <SignupContainer>
      <SignupCard>
        <SignupTitle>사업자 회원가입</SignupTitle>
        <SignupSubtitle>
          LookPick 파트너로 등록하여 다양한 혜택을 누려보세요
        </SignupSubtitle>

        <SignupForm onSubmit={handleSubmit}>
          {/* 기업 정보 섹션 */}
          <div>
            <SectionTitle>기업 정보</SectionTitle>

            <FormGroup>
              <label>사업자 등록번호 *</label>
              <input
                type="text"
                name="businessNumber"
                placeholder="사업자 등록번호를 입력하세요 (예: 123-45-67890)"
                value={formData.businessNumber}
                onChange={handleInputChange}
                required
              />
            </FormGroup>

            <FormGroup>
              <LabelWithHelp>
                <HelpIcon>
                  ?
                  <HelpTooltip>
                    국세청에서 발급하는 사업자등록증명원이 필요합니다.
                    <br />
                    사업자등록증과는 다른 서류입니다.
                  </HelpTooltip>
                </HelpIcon>
                <LabelText>기업 인증 (사업자등록증명원) *</LabelText>
              </LabelWithHelp>
              <FileUploadGroup>
                <FileUploadContainer>
                  <FileInputWrapper>
                    <HiddenFileInput
                      type="file"
                      accept=".pdf,.jpg,.jpeg,.png"
                      onChange={handleFileUpload}
                    />
                    <FileInputDisplay hasFile={!!formData.businessCertificate}>
                      <FileInfo>
                        <FileIcon>
                          {formData.businessCertificate ? "📄" : "📁"}
                        </FileIcon>
                        {formData.businessCertificate
                          ? formData.businessCertificate.name
                          : "사업자등록증명원을 선택하세요 (PDF, JPG, PNG)"}
                      </FileInfo>
                    </FileInputDisplay>
                  </FileInputWrapper>
                  <TooltipContainer>
                    <UploadButton
                      type="button"
                      onClick={handleBusinessVerification}
                      disabled={
                        !formData.businessNumber ||
                        !formData.businessCertificate
                      }
                    >
                      기업인증
                    </UploadButton>
                  </TooltipContainer>
                </FileUploadContainer>
                {verificationStatus.fileUploaded && (
                  <StatusMessage isSuccess={true}>
                    ✓ 파일이 업로드되었습니다.
                  </StatusMessage>
                )}
                {verificationStatus.businessVerified && (
                  <StatusMessage isSuccess={true}>
                    ✓ 기업인증이 완료되었습니다.
                  </StatusMessage>
                )}
              </FileUploadGroup>
            </FormGroup>

            <FormRow>
              <FormGroup>
                <label>기업명 *</label>
                <input
                  type="text"
                  name="companyName"
                  placeholder="기업명을 입력하세요"
                  value={formData.companyName}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>

              <FormGroup>
                <label>대표자명 *</label>
                <input
                  type="text"
                  name="representative"
                  placeholder="대표자명을 입력하세요"
                  value={formData.representative}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>
            </FormRow>

            <FormGroup>
              <label>회사주소 *</label>
              <input
                type="text"
                name="companyAddress"
                placeholder="회사 주소를 입력하세요"
                value={formData.companyAddress}
                onChange={handleInputChange}
                required
              />
            </FormGroup>

            <FormRow>
              <FormGroup>
                <label>개업일 *</label>
                <input
                  type="date"
                  name="establishmentDate"
                  value={formData.establishmentDate}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>

              <FormGroup>
                <label>기업 구분 *</label>
                <select
                  name="businessType"
                  value={formData.businessType}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">기업 구분을 선택하세요</option>
                  <option value="hotel">호텔/리조트</option>
                  <option value="pension">펜션/민박</option>
                  <option value="guesthouse">게스트하우스</option>
                  <option value="camping">캠핑/글램핑</option>
                  <option value="motel">모텔</option>
                  <option value="other">기타</option>
                </select>
              </FormGroup>
            </FormRow>
          </div>

          {/* 담당자 정보 섹션 */}
          <div>
            <SectionTitle>담당자 정보</SectionTitle>

            <FormGroup>
              <label>담당자명 *</label>
              <input
                type="text"
                name="managerName"
                placeholder="담당자명을 입력하세요"
                value={formData.managerName}
                onChange={handleInputChange}
                required
              />
            </FormGroup>

            <FormGroup>
              <label>휴대폰 본인인증 *</label>
              <PhoneVerificationGroup>
                <PhoneInputGroup>
                  <input
                    type="tel"
                    name="phoneNumber"
                    placeholder="휴대폰 번호를 입력하세요 (예: 010-1234-5678)"
                    value={formData.phoneNumber}
                    onChange={handleInputChange}
                    required
                  />
                  <VerifyButton
                    type="button"
                    onClick={handlePhoneVerification}
                    disabled={!formData.phoneNumber}
                  >
                    인증번호 발송
                  </VerifyButton>
                </PhoneInputGroup>

                {verificationStatus.codeSent && (
                  <VerificationCodeGroup>
                    <input
                      type="text"
                      name="verificationCode"
                      placeholder="인증번호 6자리를 입력하세요"
                      value={formData.verificationCode}
                      onChange={handleInputChange}
                      maxLength="6"
                    />
                    <VerifyButton
                      type="button"
                      onClick={handleCodeVerification}
                      disabled={!formData.verificationCode}
                    >
                      인증확인
                    </VerifyButton>
                  </VerificationCodeGroup>
                )}

                {verificationStatus.phoneVerified && (
                  <StatusMessage isSuccess={true}>
                    ✓ 휴대폰 본인인증이 완료되었습니다.
                  </StatusMessage>
                )}
              </PhoneVerificationGroup>
            </FormGroup>
          </div>

          {/* 계정 정보 섹션 */}
          <div>
            <SectionTitle>계정 정보</SectionTitle>

            <FormRow>
              <FormGroup>
                <label>아이디 *</label>
                <input
                  type="text"
                  name="userId"
                  placeholder="아이디를 입력하세요"
                  value={formData.userId}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>

              <FormGroup>
                <label>비밀번호 *</label>
                <input
                  type="password"
                  name="password"
                  placeholder="비밀번호를 입력하세요"
                  value={formData.password}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>
            </FormRow>

            <FormGroup>
              <label>비밀번호 확인 *</label>
              <input
                type="password"
                name="confirmPassword"
                placeholder="비밀번호를 다시 입력하세요"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                required
              />
            </FormGroup>
          </div>

          <SignupButton
            type="submit"
            disabled={
              !verificationStatus.businessVerified ||
              !verificationStatus.phoneVerified
            }
          >
            사업자 회원가입
          </SignupButton>
        </SignupForm>

        <SignupFooter>
          <p>
            이미 계정이 있으신가요? <Link to="/login">로그인</Link>
          </p>
        </SignupFooter>
      </SignupCard>
    </SignupContainer>
  );
};

export default SignupPage;
