import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const SignupContainer = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
  background-color: ${(props) => props.theme.colors.gray[50]};
`;

const SignupCard = styled.div`
  background-color: white;
  padding: 40px;
  border-radius: ${(props) => props.theme.borderRadius.lg};
  box-shadow: ${(props) => props.theme.shadows.md};
  width: 100%;
  max-width: 800px;
`;

const SignupTitle = styled.h1`
  text-align: center;
  margin-bottom: 10px;
  color: ${(props) => props.theme.colors.dark};
  font-size: 2rem;
`;

const SignupSubtitle = styled.p`
  text-align: center;
  margin-bottom: 40px;
  color: ${(props) => props.theme.colors.gray[600]};
  font-size: 1rem;
`;

const SignupForm = styled.form`
  display: flex;
  flex-direction: column;
  gap: 25px;
`;

const SectionTitle = styled.h3`
  color: ${(props) => props.theme.colors.dark};
  font-size: 1.2rem;
  margin-bottom: 15px;
  padding-bottom: 8px;
  border-bottom: 2px solid ${(props) => props.theme.colors.primary};
`;

const FormRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 20px;

  @media (max-width: ${(props) => props.theme.breakpoints.tablet}) {
    grid-template-columns: 1fr;
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;

  label {
    display: block;
    margin-bottom: 8px;
    font-weight: 600;
    color: ${(props) => props.theme.colors.dark};
    font-size: 0.9rem;
  }

  input,
  select {
    width: 100%;
    padding: 14px;
    border: 1px solid ${(props) => props.theme.colors.gray[300]};
    border-radius: ${(props) => props.theme.borderRadius.md};
    font-size: 16px;
    transition: border-color 0.2s ease;

    &:focus {
      border-color: ${(props) => props.theme.colors.primary};
      outline: none;
      box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
    }

    &::placeholder {
      color: ${(props) => props.theme.colors.gray[400]};
    }
  }

  select {
    cursor: pointer;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
`;

const VerifyButton = styled.button`
  padding: 14px 20px;
  background-color: ${(props) => props.theme.colors.secondary || "#6B7280"};
  color: white;
  border: none;
  border-radius: ${(props) => props.theme.borderRadius.md};
  cursor: pointer;
  font-weight: 600;
  font-size: 0.9rem;
  white-space: nowrap;
  transition: background-color 0.2s ease;
  position: relative;

  &:hover {
    background-color: ${(props) =>
      props.theme.colors.secondaryDark || "#4B5563"};
  }

  &:disabled {
    background-color: ${(props) => props.theme.colors.gray[300]};
    cursor: not-allowed;
  }
`;

const TooltipContainer = styled.div`
  position: relative;
  display: inline-block;
`;

const Tooltip = styled.div`
  position: absolute;
  bottom: 120%;
  left: 50%;
  transform: translateX(-50%);
  background-color: ${(props) => props.theme.colors.dark || "#1F2937"};
  color: white;
  padding: 12px 16px;
  border-radius: ${(props) => props.theme.borderRadius.md};
  font-size: 0.8rem;
  white-space: nowrap;
  opacity: 0;
  visibility: hidden;
  transition: opacity 0.3s ease, visibility 0.3s ease;
  z-index: 1000;
  box-shadow: ${(props) => props.theme.shadows.md};

  &::after {
    content: "";
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    border: 6px solid transparent;
    border-top-color: ${(props) => props.theme.colors.dark || "#1F2937"};
  }

  ${TooltipContainer}:hover & {
    opacity: 1;
    visibility: visible;
  }
`;

const PhoneVerificationGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const PhoneInputGroup = styled.div`
  display: flex;
  gap: 10px;
  align-items: end;

  input {
    flex: 1;
  }
`;

const VerificationCodeGroup = styled.div`
  display: flex;
  gap: 10px;
  align-items: end;

  input {
    flex: 1;
  }
`;

const StatusMessage = styled.div`
  font-size: 0.8rem;
  margin-top: 5px;
  color: ${(props) =>
    props.isSuccess
      ? props.theme.colors.success || "#10B981"
      : props.theme.colors.danger || "#EF4444"};
`;

const SignupButton = styled.button`
  padding: 16px;
  background-color: ${(props) => props.theme.colors.primary};
  color: white;
  border: none;
  border-radius: ${(props) => props.theme.borderRadius.md};
  cursor: pointer;
  font-weight: bold;
  font-size: 18px;
  margin-top: 20px;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: ${(props) => props.theme.colors.primaryDark};
  }

  &:disabled {
    background-color: ${(props) => props.theme.colors.gray[300]};
    cursor: not-allowed;
  }
`;

const SignupFooter = styled.div`
  text-align: center;
  margin-top: 30px;

  a {
    color: ${(props) => props.theme.colors.primary};
    text-decoration: none;
    font-weight: 600;

    &:hover {
      text-decoration: underline;
    }
  }
`;

const SignupPage = () => {
  const [formData, setFormData] = useState({
    // 기업 정보
    businessNumber: "",
    companyName: "",
    representative: "",
    companyAddress: "",
    establishmentDate: "",
    businessType: "",

    // 담당자 정보
    managerName: "",
    phoneNumber: "",
    verificationCode: "",

    // 계정 정보
    userId: "",
    password: "",
    confirmPassword: "",
  });

  const [verificationStatus, setVerificationStatus] = useState({
    businessVerified: false,
    phoneVerified: false,
    codeSent: false,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleBusinessVerification = () => {
    // 사업자 등록번호 검증 로직
    console.log("사업자 등록번호 검증:", formData.businessNumber);
    setVerificationStatus((prev) => ({ ...prev, businessVerified: true }));
  };

  const handlePhoneVerification = () => {
    // 휴대폰 인증번호 발송 로직
    console.log("인증번호 발송:", formData.phoneNumber);
    setVerificationStatus((prev) => ({ ...prev, codeSent: true }));
  };

  const handleCodeVerification = () => {
    // 인증번호 확인 로직
    console.log("인증번호 확인:", formData.verificationCode);
    setVerificationStatus((prev) => ({ ...prev, phoneVerified: true }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("사업자 회원가입 시도:", formData);
  };

  return (
    <SignupContainer>
      <SignupCard>
        <SignupTitle>사업자 회원가입</SignupTitle>
        <SignupSubtitle>
          LookPick 파트너로 등록하여 다양한 혜택을 누려보세요
        </SignupSubtitle>

        <SignupForm onSubmit={handleSubmit}>
          {/* 기업 정보 섹션 */}
          <div>
            <SectionTitle>기업 정보</SectionTitle>

            <FormGroup>
              <label>사업자 등록번호 *</label>
              <ButtonGroup>
                <input
                  type="text"
                  name="businessNumber"
                  placeholder="사업자 등록번호를 입력하세요 (예: 123-45-67890)"
                  value={formData.businessNumber}
                  onChange={handleInputChange}
                  required
                />
                <TooltipContainer>
                  <VerifyButton
                    type="button"
                    onClick={handleBusinessVerification}
                    disabled={!formData.businessNumber}
                  >
                    기업인증
                  </VerifyButton>
                  <Tooltip>사업자 등록증명원 파일 첨부가 필요합니다</Tooltip>
                </TooltipContainer>
              </ButtonGroup>
              {verificationStatus.businessVerified && (
                <StatusMessage isSuccess={true}>
                  ✓ 사업자 등록번호가 확인되었습니다.
                </StatusMessage>
              )}
            </FormGroup>

            <FormRow>
              <FormGroup>
                <label>기업명 *</label>
                <input
                  type="text"
                  name="companyName"
                  placeholder="기업명을 입력하세요"
                  value={formData.companyName}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>

              <FormGroup>
                <label>대표자명 *</label>
                <input
                  type="text"
                  name="representative"
                  placeholder="대표자명을 입력하세요"
                  value={formData.representative}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>
            </FormRow>

            <FormGroup>
              <label>회사주소 *</label>
              <input
                type="text"
                name="companyAddress"
                placeholder="회사 주소를 입력하세요"
                value={formData.companyAddress}
                onChange={handleInputChange}
                required
              />
            </FormGroup>

            <FormRow>
              <FormGroup>
                <label>개업일 *</label>
                <input
                  type="date"
                  name="establishmentDate"
                  value={formData.establishmentDate}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>

              <FormGroup>
                <label>기업 구분 *</label>
                <select
                  name="businessType"
                  value={formData.businessType}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">기업 구분을 선택하세요</option>
                  <option value="hotel">호텔/리조트</option>
                  <option value="pension">펜션/민박</option>
                  <option value="guesthouse">게스트하우스</option>
                  <option value="camping">캠핑/글램핑</option>
                  <option value="motel">모텔</option>
                  <option value="other">기타</option>
                </select>
              </FormGroup>
            </FormRow>
          </div>

          {/* 담당자 정보 섹션 */}
          <div>
            <SectionTitle>담당자 정보</SectionTitle>

            <FormGroup>
              <label>담당자명 *</label>
              <input
                type="text"
                name="managerName"
                placeholder="담당자명을 입력하세요"
                value={formData.managerName}
                onChange={handleInputChange}
                required
              />
            </FormGroup>

            <FormGroup>
              <label>휴대폰 본인인증 *</label>
              <PhoneVerificationGroup>
                <PhoneInputGroup>
                  <input
                    type="tel"
                    name="phoneNumber"
                    placeholder="휴대폰 번호를 입력하세요 (예: 010-1234-5678)"
                    value={formData.phoneNumber}
                    onChange={handleInputChange}
                    required
                  />
                  <VerifyButton
                    type="button"
                    onClick={handlePhoneVerification}
                    disabled={!formData.phoneNumber}
                  >
                    인증번호 발송
                  </VerifyButton>
                </PhoneInputGroup>

                {verificationStatus.codeSent && (
                  <VerificationCodeGroup>
                    <input
                      type="text"
                      name="verificationCode"
                      placeholder="인증번호 6자리를 입력하세요"
                      value={formData.verificationCode}
                      onChange={handleInputChange}
                      maxLength="6"
                    />
                    <VerifyButton
                      type="button"
                      onClick={handleCodeVerification}
                      disabled={!formData.verificationCode}
                    >
                      인증확인
                    </VerifyButton>
                  </VerificationCodeGroup>
                )}

                {verificationStatus.phoneVerified && (
                  <StatusMessage isSuccess={true}>
                    ✓ 휴대폰 본인인증이 완료되었습니다.
                  </StatusMessage>
                )}
              </PhoneVerificationGroup>
            </FormGroup>
          </div>

          {/* 계정 정보 섹션 */}
          <div>
            <SectionTitle>계정 정보</SectionTitle>

            <FormRow>
              <FormGroup>
                <label>아이디 *</label>
                <input
                  type="text"
                  name="userId"
                  placeholder="아이디를 입력하세요"
                  value={formData.userId}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>

              <FormGroup>
                <label>비밀번호 *</label>
                <input
                  type="password"
                  name="password"
                  placeholder="비밀번호를 입력하세요"
                  value={formData.password}
                  onChange={handleInputChange}
                  required
                />
              </FormGroup>
            </FormRow>

            <FormGroup>
              <label>비밀번호 확인 *</label>
              <input
                type="password"
                name="confirmPassword"
                placeholder="비밀번호를 다시 입력하세요"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                required
              />
            </FormGroup>
          </div>

          <SignupButton
            type="submit"
            disabled={
              !verificationStatus.businessVerified ||
              !verificationStatus.phoneVerified
            }
          >
            사업자 회원가입
          </SignupButton>
        </SignupForm>

        <SignupFooter>
          <p>
            이미 계정이 있으신가요? <Link to="/login">로그인</Link>
          </p>
        </SignupFooter>
      </SignupCard>
    </SignupContainer>
  );
};

export default SignupPage;
